// Mini Chart.js implementation for trend graph
function drawTrendChart(canvas, data) {
    // MOBILE FIX: Resize canvas to match displayed size
    const rect = canvas.getBoundingClientRect();
    const dpr = window.devicePixelRatio || 1;
    
    // Set canvas internal dimensions to match displayed size
    if (rect.width > 0 && rect.height > 0) {
        canvas.width = rect.width * dpr;
        canvas.height = Math.max(rect.height * dpr, 300 * dpr); // Min height 300px
        
        // Scale context to account for device pixel ratio
        const ctx = canvas.getContext('2d');
        ctx.scale(dpr, dpr);
    }
    
    const ctx = canvas.getContext('2d');
    const width = canvas.width / dpr;
    const height = canvas.height / dpr;
    
    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    
    if (!data || data.length === 0) {
        ctx.fillStyle = '#999';
        ctx.font = '16px Arial';
        ctx.textAlign = 'center';
        ctx.fillText('No data yet - play some rounds!', width/2, height/2);
        return;
    }
    
    const padding = 40;
    const chartWidth = width - 2 * padding;
    const chartHeight = height - 2 * padding;
    
    // Find max value
    const maxValue = Math.max(...data.map(d => Math.max(d.player, d.computer)));
    const minValue = 0;
    
    // Draw axes
    ctx.strokeStyle = '#ccc';
    ctx.lineWidth = 2;
    ctx.beginPath();
    ctx.moveTo(padding, padding);
    ctx.lineTo(padding, height - padding);
    ctx.lineTo(width - padding, height - padding);
    ctx.stroke();
    
    // Draw player line (blue)
    ctx.strokeStyle = '#667eea';
    ctx.lineWidth = 3;
    ctx.beginPath();
    data.forEach((point, i) => {
        const x = padding + (i / (data.length - 1 || 1)) * chartWidth;
        const y = height - padding - (point.player / maxValue) * chartHeight;
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
    });
    ctx.stroke();
    
    // Draw computer line (red)
    ctx.strokeStyle = '#ef4444';
    ctx.lineWidth = 3;
    ctx.beginPath();
    data.forEach((point, i) => {
        const x = padding + (i / (data.length - 1 || 1)) * chartWidth;
        const y = height - padding - (point.computer / maxValue) * chartHeight;
        if (i === 0) ctx.moveTo(x, y);
        else ctx.lineTo(x, y);
    });
    ctx.stroke();
    
    // Legend
    ctx.font = '14px Arial';
    ctx.fillStyle = '#667eea';
    ctx.fillText('● You', 60, 20);
    ctx.fillStyle = '#ef4444';
    ctx.fillText('● Computer', 140, 20);
}

